refsection.programlisting:

#include <stack-c.h>

int sci_check_properties(char * fname)
{
  int m1, n1, l1;
  int m2, n2, l2;

  CheckRhs(2,2);

  GetRhsVar(1, "d", &m1, &n1, &l1);
  GetRhsVar(2, "d", &m2, &n2, &l2);

  // Check compatibility beetween arg 1 and arg 2. We want m1*n1 == n2 
  CheckDimProp(1,2, m1 * n1 != n2); 

  return 0;
}
