StrErr printf_info(int _iVar);

int commom_function(char *fname,unsigned long fname_len)
{
    StrErr strErr;
    int i;
    int *piAddr1 = NULL;

    for(i = 0 ; i < Rhs ; i++)
    {
        strErr = printf_info(i + 1);
        if(strErr.iErr)
        {
            printError(&strErr, 0);
            break;
        }
        sciprint("\n\n");
    }

    //1 for true, 0 for false
    int iBool = strErr.iErr == 0 ? 1 : 0;
    strErr = createMatrixOfBoolean(pvApiCtx, 1, 1, 1, &iBool);
    if(strErr.iErr)
    {
        printError(&strErr, 0);
        return 0;
    }
    //assign allocated variables to Lhs position
    LhsVar(1) = 1;

    return 0;
}

StrErr printf_info(int _iVar)
{
    StrErr strErr;
    int* piAddr     = NULL;
    int iType       = 0;
    int iRows       = 0;
    int iCols       = 0;
    int iItem       = 0;
    int iComplex    = 0;

    strErr = getVarAddressFromPosition(pvApiCtx, _iVar, &piAddr);
    if(strErr.iErr)
    {
        return strErr;
    }

    sciprint("Variable %d information:\n", _iVar);

    strErr = getVarType(pvApiCtx, piAddr, &iType);
    if(strErr.iErr)
    {
        return strErr;
    }

    sciprint("\tType: ");
    switch(iType)
    {
        case sci_matrix : 
            sciprint("double\n");
            break;
        case sci_poly : 
            sciprint("polynomial\n");
            break;
        case sci_boolean : 
            sciprint("boolean\n");
            break;
        case sci_sparse : 
            sciprint("sparse\n");
            break;
        case sci_boolean_sparse : 
            sciprint("boolean_sparse\n");
            break;
        case sci_ints : 
        {
            char pstSigned[]    = "signed";
            char pstUnsigned[]  = "unsigned";
            char* pstSign       = pstSigned;

            int iPrec           = 0;
            strErr = getMatrixOfIntegerPrecision(pvApiCtx, piAddr, &iPrec);
            if(strErr.iErr)
            {
                return strErr;
            }

            if(iPrec > 10)
            {
                pstSign = pstUnsigned;
            }

            sciprint("%s integer %d bits\n", pstSign, (iPrec % 10) * 8);
        }
        break;
        case sci_strings : 
            sciprint("strings\n");
            break;
        case sci_list : 
            sciprint("list\n");
            break;
        case sci_tlist : 
            sciprint("tlist\n");
            break;
        case sci_mlist : 
            sciprint("mlist\n");
            break;
        default :
            sciprint("Not manage by this function\n");
            return strErr;
    }

    if(isVarComplex(pvApiCtx, piAddr))
    {
        sciprint("\tComplex: Yes\n");
    }

    sciprint("\tDimensions: ");
    if(isVarMatrixType(pvApiCtx, piAddr))
    {
        strErr = getVarDimension(pvApiCtx, piAddr, &iRows, &iCols);
        if(strErr.iErr)
        {
            return strErr;
        }

        sciprint("%d x %d", iRows, iCols);
    }
    else
    {
        strErr = getListItemNumber(pvApiCtx, piAddr, &iItem);
        if(strErr.iErr)
        {
            return strErr;
        }
        sciprint("%d", iItem);
    }
    return strErr;
}
