function x = %c_getSize(a)
    x = length(a);
endfunction

function x = %s_getSize(a)
    x = size(a);
endfunction


a = [1,2,3,4];
a_ref = [2,2];
assert_checkequal(getSize(a) == a_ref);

b = ["May","the","puffin";"be","with","you"];
b_ref = [3,3,6;2,4,3];
assert_checkequal(getSize(b) == b_ref);
