refsection.programlisting:
// 簡単な例
a=list(1,2,3);
tree_show(a);
// mlistを指定した他の例
str=['a','b','c','d','e','f','g','h'];
n=matrix(str, [2,2,2]);
v=matrix(1:8, [2,2,2]);
Mm=mlist(['V','name','value'],n,v);
tree_show(Mm);
// tlistを指定した別の例
Mt=tlist(['V','name','value'],['a','b','c'],[1 2 3]);
tree_show(Mt);
