M=mlist(['V','name','value'],['a','b';'c' 'd'],[1 2; 3 4]); //Definindo exibição function %V_p(M),disp(M.name+':'+string(M.value)),endfunction
//Definindo operação de extração function r=%V_e(varargin) M=varargin($) r=mlist(['V','name','value'],M.name(varargin(1:$-1)),M.value(varargin(1:$-1))) endfunction M(2,:) // a segunda entrada do vetor M M.value
//Definindo operações de inserção function M=%V_i_V(varargin) M=varargin($) N=varargin($-1) M.value(varargin(1:$-2))=N.value M.name(varargin(1:$-2))=N.name endfunction M(1,1)=M(2,2)
function M=%s_i_V(varargin) //inserção de uma matriz regular em uma matriz V M=varargin($) N=varargin($-1) M.value(varargin(1:$-2))=N M.name(varargin(1:$-2))=emptystr(N) endfunction M(1,1)=44
//caso de tlists M=tlist(['V','name','value'],['a','b';'c' 'd'],[1 2; 3 4]); M(2) M(2)='a'+string([1 2;3 4])
M('name')
