// Интегрирование ОДУ даёт, например, два решения yex и yex1.

function y=f(c)
    y=c.*(1-tanh(sqrt(c)/4).^2)-2;
endfunction
c=fsolve(2,f);

function y=yex(x,c)
  y=log(c/2*(1-tanh(sqrt(c)*(1/4-x/2)).^2))
endfunction

function y=f1(c1)
    y=2*c1^2+tanh(1/4/c1)^2-1;
endfunction
c1=fsolve(0.1,f1);

function y=yex1(x,c1)
  y=log((1-tanh((2*x-1)/4/c1).^2)/2/c1/c1)
endfunction

