XF = [XF xf]; YF = [YF yf]; ZF = [ZF zf];
end

for j=1:ny
[xf,yf,zf] = nf3d(matrix(X(:,j,:),[nx,nz]),..
matrix(Y(:,j,:),[nx,nz]),..
matrix(Z(:,j,:),[nx,nz]));
XF = [XF xf]; YF = [YF yf]; ZF = [ZF zf];
end

clf()
