refsection.programlisting:
     absptr=funptr("abs");// get the function_ptr associated with the abs function
     newfun("abs_bis",absptr) // create an alias of the abs function
     abs_bis(-1+%i)
     clearfun("abs") // remove abs as a known function

      //redefine abs
      function y=abs(x)
	y=abs_bis(real(x));
      endfunction

     abs(-1+%i)
