refsection.programlisting:
     absptr=funptr("abs");// получить function_ptr, связанный с функцией abs
     newfun("abs_bis",absptr) // создать псевдоним функции abs
     abs_bis(-1+%i)
     clearfun("abs") // удалить abs, как известную функцию

     //переопределить abs
     function y=abs(x)
	y=abs_bis(real(x));
     endfunction

     abs(-1+%i)
