refsection.programlisting:
function z = F(x, y)
    z = x*(x^2 + y^2) - 5*(x^2 - y^2);
endfunction

x_range = -3:0.1:6;
y_range = -5:0.1:5;

// Draw the curve in the [-3 6] x [-5 5] range
implicitplot(F, x_range, y_range)

