refsection.programlisting:
function z = ocho(x, y)
    z = x*sin(x) - y^2*cos(y);
endfunction

x_range = linspace(-%pi/2, %pi/2, 200);
y_range = x_range;

f_eval = feval(x_range, y_range, ocho);

// Draws f_eval = 0
implicitplot(f_eval, x_range, y_range)

