// x, y and z initialisation
t = 1:200
x = t.*cos(t).*sin(t)
y = t.*sin(t).*sin(t)
z = t.*cos(t)
// create 3D scatter plot
scatter3(x,y,z)
// modify rotation angles
set(gca(),"rotation_angles",[60,45])

// x, y and z initialisation
t = 1:200
x = t.*cos(t).*sin(t)
y = t.*sin(t).*sin(t)
z = t.*cos(t)
// size according to radius
s = linspace(50,1,length(t))
// create 3D scatter plot
scatter3(x,y,z,s)
// modify rotation angles
set(gca(),"rotation_angles",[60,45])

