refsection.programlisting:
t = [0:0.3:2*%pi]';
z = sin(t)*cos(t');
plot3d1(t,t,z)
f = gcf();
for h = 0:0.1:1
  hsv = [h*ones(32,1) linspace(0,1,32)' 0.7*ones(32,1)];
  f.color_map = hsv2rgb(hsv);
  sleep(10)
end
for v = 0:0.1:1
  hsv = [ones(32,1) linspace(0,1,32)' v*ones(32,1)];
  f.color_map = hsv2rgb(hsv);
  sleep(10)
end

