refsection.programlisting:
x=linspace(0,1,9)';
y=x.^3;
clf();
plot(x,y);
e=gce();p=e.children(1);//get the handle on the polyline
p.mark_mode="on";p.mark_style=2;p.mark_size=12;
t=datatipCreate(p,3);

datatipCreate(p,[0.6,0.2]);
datatipSetInterp(p,%t);
datatipCreate(p,[0.82,0.62]);

