xlfont('reset');
xlfont()

// Caution : this example may not work if your system have not Monospaced font.
xlfont("Monospaced",10,%t,%t);
xstring(1,0,'A title');
figure_entity = gcf();
axes_entity = figure_entity.children;
title_entity = axes_entity.children;
title_entity.font_style = 10;

xlfont('reset');
