refsection.programlisting:
// x 初期化
x=[0:0.1:2*%pi]';
//simple plot
plot(sin(x))

//複数プロット
x=[0:0.1:2*%pi]';
plot(x,[sin(x) sin(2*x) sin(3*x)])

clf()
x=[0:0.1:2*%pi]';
// 右に軸
plot(x,sin(x))
a=gca(); // カレントの軸エンティティのハンドル
a.y_location ="right";

x=[0:0.1:2*%pi]';
// (0,0)を中心とする軸
plot(x-4,sin(x),x+2,cos(x))
a=gca(); // 軸エンティティのハンドル
a.x_location = "origin";
a.y_location = "origin";
// plotにより作成されたエンティティに複数の処理を行う
isoview()
a.children // 軸の子のリスト : ここでは,2個のエンティティの複合子オブジェクト
poly1= a.children.children(2); //線分群のハンドルをpoly1 に保存
poly1.foreground = 4; // スタイルを変更する別の方法...
poly1.thickness = 3;  // ...曲線の太さ.
poly1.clip_state='off' // 制御をクリップ
isoview("off")

refsection.scilab:image:
x=[0:0.1:2*%pi]';
