refsection.programlisting:
x=[0:0.1:2*%pi]';

f=get("default_figure"); // 規範となる図のハンドルを取得

a=get("default_axes");   // 規範となる軸のハンドルを取得

                         // そのプロパティを設定

f.figure_size=[1200 900];

f.figure_position=[0 0];

a.background=4;

a.box="off";

a.foreground=5;

a.labels_font_color=25;

a.labels_font_size=4;

a.sub_tics=[7 3];

a.x_location="middle";

a.y_location="middle";

a.tight_limits="on";

a.thickness=2;

a.grid=[-1 24];

subplot(221);

plot2d(x-2,sin(x))

subplot(222);

plot2d(x-6,[2*cos(x)+.7 2*cos(x)+.9 cos(2*x) .2+sin(3*x)],[-1,-2,-3 -4])

sda() // return to the  default values of the axes' model

subplot(223);

plot2d(x-2,sin(x))

subplot(224);

plot2d(x-6,[2*cos(x)+.7 2*cos(x)+.9 cos(2*x) .2+sin(3*x)],[-1,-2,-3 -4])

xdel(0)

plot2d(x-2,sin(x))
