refsection.programlisting:

clf()
x=0:0.01:6*%pi;
plot2d(x,sin(x^2))
zoom_rect([16,-1,18,1])
//もっとズーム
zoom_rect([16,0,16.2,1])
//元に戻す
unzoom()
// axes_propertiesによりズーム
a=gca();
a.zoom_box=[16,0,16.2,1];
a.zoom_box=[];

//サブプロットを適当にズーム
clf()
x=0:0.01:6*%pi;
subplot(211)
plot2d(x,cos(x))
subplot(212)
plot2d(x,cos(2*x))
rect=[3 -2 7 10]; //カレントの軸(直近の軸)で指定された矩形座標
zoom_rect(rect) 
unzoom()
//グローバルな軸をカレントの軸に設定
f=gcf();set('current_axes',f.children($)) 
rect=[0.4 0 0.6 1] //ウインドウの大きさの比で指定した矩形
zoom_rect(rect) 
rect=[0.4 0.2 0.6 0.8]; //ウインドウの大きさの比で指定した矩形
zoom_rect(rect)

// カレントの図を対話的にズーム
zoom_rect();
// or
zoom_rect(gcf());
