refsection.programlisting:
// 例 1

x = linspace(0,1,81);

z = cos(2*%pi*x)'*sin(2*%pi*x);

zm = min(z); zM = max(z);

clf()

xset("colormap",jetcolormap(64))

colorbar(zm,zM)

Sgrayplot(x,x,z)

xtitle("The function cos(2 pi x)sin(2 pi y)")

