refsection.programlisting:
// 例 2

x = linspace(0,1,81);

z = cos(2*%pi*x)'*sin(2*%pi*x);

zm = min(z); zM = max(z);

zz = abs(0.5*cos(2*%pi*x)'*cos(2*%pi*x));

zzm = min(zz); zzM = max(zz);

clf();

xset("colormap",jetcolormap(64))



drawlater() ;

subplot(2,2,1)

   colorbar(zm,zM)

   Sgrayplot(x,x,z, strf="031", rect=[0 0 1 1])

   xtitle("a Sgrayplot with a colorbar")

subplot(2,2,2)

   colorbar(zm,zM)

   plot3d1(x,x,z)

   xtitle("a plot3d1 with a colorbar")

subplot(2,2,3)

   colorbar(zzm,zzM)

   plot3d1(x,x,zz)

   xtitle("a plot3d1 with a colorbar")

subplot(2,2,4)

   colorbar(zzm,zzM)

   Sgrayplot(x,x,zz, strf="031", rect=[0 0 1 1])

   xtitle("a Sgrayplot with a colorbar")

drawnow() ;

