refsection.programlisting:
// 軸を表示

axes = gca() ;

axes.axes_visible = 'on' ;

axes.data_bounds = [ 1, 1 ; 10, 10 ] ;



// 軸にラベルを表示

xtitle( 'stringbox', 'X', 'Y' )  ;



// Xラベルのバウンディングボックスを取得

stringbox( axes.x_label )



// 文字列を描画

str = [ "Scilab", "is" , "not", "Skylab" ] ;

xstring( 4, 9, str ) ;



//テキストを修正

e = gce() ;

e.font_angle = 90 ;

e.font_size    = 6  ;

e.font_style   = 7 ;

e.box = 'on' ;



// バウンディングボックスを取得

stringbox( e )

// または

rect = stringbox( str, 4, 9, 90, 7, 6 )



// クリックし,テキストがヒットしたかどうかを確認

hit = xclick()  ;

hit = hit( 2 : 3 ) ;



if hit(1) >= rect(1,1)  & hit(1) <=  rect(1,2) & hit(2) <= rect(2,2) & hit(2) >= rect(2,3) then

  disp('You hit the text.') ;

else

  disp('You missed it.')

end;

refsection.scilab:image:
axes = gca() ;

axes.axes_visible = 'on' ;

axes.data_bounds = [ 1, 1 ; 10, 10 ] ;

xtitle( 'stringbox', 'X', 'Y' )  ;

stringbox( axes.x_label )

str = [ "Scilab", "is" , "not", "Skylab" ] ;

xstring( 4, 9, str ) ;
