refsection.programlisting:
// 最初のサブウインドウ

xsetech([0,0,1.0,0.5])

plot2d()

// 続いてxsetechにより2番目のサブウインドウを設定

xsetech([0,0.5,1.0,0.5])

grayplot()

// 最初のサブウインドウのグラフィックスケールを取得

xsetech([0,0,1.0,0.5])

[wrect,frect,logflag,arect]=xgetech();

// 2番目のサブウインドウのグラフィックスケールを取得

xsetech([0,0.5,1.0,0.5])

[wrect,frect,logflag,arect]=xgetech();

clf();

xset('default')
