for i = 1:4
    subplot(1,4,i)
    plot3d([0 1],[0 1],[0 1])
    a = gca();
    a.box = box(i);
    a.rotation_angles = [235 326];
    title(msprintf("%s", box(i)), "fontsize",3)
end
