refsection.programlisting:
function y = foo(a,b,c)
   y = a + 2 * b + c;
endfunction

path = help_skeleton('foo', TMPDIR)
if (isdef('editor') | (funptr('editor')&lt;&gt;0)) then
   editor(path);
end

