para.latex:
\begin{eqnarray}
\begin{array}{l}
\textrm{minimize } c^T \cdot x\\
A_{eq} x = b_{eq}\\
x \geq 0
\end{array}
\end{eqnarray}

\begin{eqnarray}
\begin{array}{l}
\textrm{minimize } c^T \cdot x\\
A_{eq} x = b_{eq}\\
A x \leq b\\
\ell_b \leq x \leq u_b
\end{array}
\end{eqnarray}

\begin{eqnarray}
\begin{array}{l}
\textrm{minimize } -x_1 -x_2\\
x_1 - x_2 = 0\\
x_1 + x_2 + x_3 = 2\\
x \geq 0
\end{array}
\end{eqnarray}

refsection.programlisting:
n=10;
p=20;
Aeq=rand(n,p);
c=rand(p,1);
x0=rand(p,1);
beq=Aeq*x0;
xopt=karmarkar(Aeq,beq,c,x0);
// Check constraints
norm(Aeq*xopt-beq)

para.latex:
\begin{eqnarray}
\begin{array}{l}
\textrm{minimize } - 20 x_1 - 24 x_2\\
3 x_1 + 6 x_2 \leq 60 \\
4 x_1 + 2 x_2 \leq 32 \\
\end{array}
\end{eqnarray}

\begin{eqnarray}
\begin{array}{l}
\textrm{minimize } 2 x_1 + 5 x_2 - 2.5 x_3\\
x_1 +   S4 x_3 \leq 5 \\
E2 x1 - x2 - x3 \leq 0 \\
-2 \leq x_1 \leq 2 \\
1 \leq x_2 \\
0 \leq x_3 \leq 3 \\
\end{array}
\end{eqnarray}

\begin{eqnarray}
\begin{array}{l}
\textrm{minimize } - x_1 - x_2\\
2 w x_1 + x_2 \leq 1+w^2, \quad w=0.0, 0.1, 0.2, ..., 1.0\\
x_1,x_2 \geq 0
\end{array}
\end{eqnarray}

refsection.programlisting:
n=11;
A = [2*linspace(0,1,n)',ones(n,1)];
b = 1 + linspace(0,1,n)'.^2;
c=[-1;-1];
// Plot the constraints
scf();
for i = 1 : n
  plot(linspace(0,1,100),b(i)-A(i,1)*linspace(0,1,100),"b-")
end
// Run the optimization
xopt=karmarkar([],[],c,[],[],[],[],myoutputfunction,A,b);
// Plot the starting and ending points
plot(xopt(1),xopt(2),"k*")

refsection.scilab:image:
function stop = myoutputfunction ( xopt , optimValues , state )
plot(xopt(1),xopt(2),"bo")
stop = %f
endfunction

n=11;
A = [2*linspace(0,1,n)',ones(n,1)];
b = 1 + linspace(0,1,n)'.^2;
c=[-1;-1];
for i = 1 : n
plot(linspace(0,1,100),b(i)-A(i,1)*linspace(0,1,100),"b-")
end
xopt=karmarkar([],[],c,[],[],[],[],myoutputfunction,A,b);
plot(xopt(1),xopt(2),"k*")

refsection.programlisting:
// An infeasible problem.
// Minimize -3 x1 + x2
//  - x1 -   x2 >= -2
//  2 x1 + 2 x2 >= 10
// x >= 0
c = [-3;1];
A=[
-1 -1
2  2
];
A=-A;
b=[-2;10];
b=-b;
lb=[0;0];
[xopt,fopt,exitflag,iter,yopt]=karmarkar([],[],c,[],[],[],[],[],A,b,lb)

// An unbounded problem.
// Minimize -x1 - 2 x2
//  -1 x1 +   x2 <= 2
//  -2 x1 +   x2 <= 1
// x >= 0
c = [-1;-2];
A=[
-1  1
-2  1
];
b=[2;1];
lb=[0;0];
[xopt,fopt,exitflag,iter,yopt]=karmarkar([],[],c,[],[],[],[],[],A,b,lb)
