refsection.programlisting:
clf()
N = 140;
w1 = window('kr', N, 1);
w2 = window('kr', N, 2);
w4 = window('kr', N, 4);
w8 = window('kr', N, 8);
w16 = window('kr', N, 16);

// Plot the window profile
plot((1:N)', [w1; w2; w4; w8; w16]')
set(gca(), 'grid', [1 1]*color('gray'))
legend("$\beta = "+string([1;2;4;8;16])+'$',[55,0.3])
xlabel("n")
ylabel("w_n")
title(gettext("Comparison of Kaiser window profiles"))

