refsection.programlisting:
// 行列のエントリは一様分布となります.
W=sprand(100,1000,0.001);
// 行列のエントリは正規分布となります.
W=sprand(100,1000,0.001,"normal");

typ = "normal";
// typ = "uniform";
nrows = 1000;
ncols = 2000;
density = 1/100;
s=sprand(nrows,ncols,density,typ);
nnzs=nnz(s);
[ij,v]=spget(s);
[%inf -%inf 0 %inf 1] // Limit values for "normal"
[nnzs min(v) mean(v) max(v) variance(v)]
[%inf 0 0.5 1 1/12] // Limit values for "uniform"

kmax = 1000;
ncols=10;
nrows=15;
density=0.01;
typ="uniform";
C=zeros(nrows,ncols);
R=[];
for k=1:kmax
  M=sprand(nrows,ncols,density,typ);
  NZ=find(M<>0);
  NZratio = size(NZ,"*")/(nrows*ncols);
  R=[R NZratio];
  C(NZ)=C(NZ)+1;
end

// A_ijの期待値は
density * kmax
// この値を実際のイベントと比較 :
C
// 平均値は期待値に近くなります.
[density*kmax mean(C)]
// 密度は密度の期待値に近くなります
[density mean(R)]
