listitem.para:
Defines the key for the fontFamily style.
                            Possible values are names such as
                            Arial;
                            Dialog;
                            Verdana;
                            Times New Roman. The value is of
                            type String.

para.programlisting:
loadXcosLibs();
pal = xcosPal();

o1 = SUMMATION("define");
o2 = BIGSOM_f("define");

pal = xcosPalAddBlock(pal, o1);
pal = xcosPalAddBlock(pal, o2);

xcosPalAdd(pal);

loadXcosLibs;
//Create a simple custom block.
function [x,y,typ]=MyBlock(job,arg1,arg2)
  x=[];y=[];typ=[];
  select job
    case 'set' then
      x=arg1;
      messagebox('No settings for a MyBlock block type');
    case 'define' then
      model = scicos_model();
      model.sim = list('ramp',4);
      model.blocktype = 'c';
      model.out = 1;
      x=standard_define([2 2],model,[],[])
  end
endfunction

//create an instance of a new block
o = MyBlock("define");

//create a new palette
pal = xcosPal("My palette");
//add block to this palette using e.g. RAMP icon
pal = xcosPalAddBlock(pal,..
        o,..
        SCI + "/modules/xcos/images/palettes/RAMP.png",..
        SCI + "/modules/xcos/images/blocks/RAMP.svg");

//deploy palette
xcosPalAdd(pal);

loadXcosLibs;
//Create a simple custom block.
function [x,y,typ]=MyBlock(job,arg1,arg2)
  x=[];y=[];typ=[];
  select job
    case 'set' then
      x=arg1;
      messagebox('No settings for a MyBlock block type');
    case 'define' then
      model = scicos_model();
      model.sim = list('ramp',4);
      model.blocktype = 'c';
      model.out = 1;
      x=standard_define([2 2],model,[],[])
  end
endfunction

// Customize the style
style = struct();
style.fillColor="red";

block_img = SCI + "/modules/xcos/images/blocks/RAMP.svg";
// protect drive letter
if getos() == "Windows" then
    block_img = "/" + block_img;
end
style.image="file://" + block_img;

//create an instance of a new block
o = MyBlock("define");

//create a new palette
pal = xcosPal("My palette");
//add block to this palette using e.g. RAMP icon
pal = xcosPalAddBlock(pal,..
        o,..
        SCI + "/modules/xcos/images/palettes/RAMP.png",..
        style);

//deploy palette
xcosPalAdd(pal);
